import styled from "styled-components";

export const CardContainer = styled.div`
    display:flex;
    flex-direction:row;
    justify-content:space-between;
    width:100%;
    margin:auto;
    background-color: #9cd3d3;
    padding:2em;
    border-radius:15px;
    background: rgb(131,58,180);
    background: linear-gradient(90deg, rgba(131,58,180,1) 0%, rgba(29,253,102,1) 49%, rgba(252,176,69,1) 100%);
    
`;
