import Cards from './components/Cards/Cards.jsx';
import Nav from './components/Nav/Nav.jsx';
import style from './App.module.css';
import { useEffect, useState } from 'react' ;
import {Route, Routes, useLocation, useNavigate} from "reac-router-dom";
import Detail from "./components/Detail/Detail.jsx";
import About from "*/components/About/About.jsx";
import Form from "./components/Form/Form"

function App () {

  //! HOOKS
  const {pathname} = useLocation();
  const [access, setAccess] = useState(false);
  const [characters, setCharacters] = useState([]);
  const navigate = useNavigate();

  useEffect (() => {
    !access  && navigate ("/");
  }, [access]);


 //! CREDENCIALES FALSAS
  const username = "ders@gmail.com";
  const password = "claveprueba321";


 //! MANEJADORES DE EVENTOS
  const onSearch = (id) => {
    const URL_BASE = "https://be-a-rym.u.railway.app/api"
    const KEY ="216f04036b13.036a1a7847f0b793f2f5";
    if(characters.find((char) =>char.id === id)) {
      return alert("Pj repetido");
    }

    fetch(`${URL_BASE}/character/${id}?key=${KEY}`)
    .then((response) => response.json())
    .then((data) => {
      if (data.name) {
        setCharacters((oldChars) => [...oldChars, data]);
      } else {
        alert("Algo salió mal");
      }
    });  
  };

  const onClose = (id) => {
    setCharacters(characters.filter((char) => char.id !== id));
  };

  const login = (useData) => {
    if(useData.username ===username && userData.password === password) {
      setAccess(true);
      navigate("/home");
    } else {
      alert("Credenciales incorrectas");
    }
  };


  // ! RENDER
  return (
    <div>
      {pathname !== "/" && <Nav onSearch={onSearch}/>}
      <Routes>
        <Route path="/" element={<Form login={login}/>}/>
        <Route
        path="/home"
        element = {< Cards characters={characters} onClose={onClose} />}
        />
        <Route path="/about" element={<About/>}/>
        <Route path="/detail/:detailID" element={<Detail/>}/>
      </Routes>
    </div>
  )
}

export default App


